def get_data(obj_names):
    """
    Fetch data from the workspace your narrative lives in.
    """
    from biokbase.workspace.client import Workspace
    import os

    ws = Workspace('https://kbase.us/services/ws')
    ws_name = os.environ['KB_WORKSPACE_ID']
    if type(obj_names) is list:
        lookup_list = [{'workspace':ws_name, 'name':name} for name in obj_names]
        return ws.get_objects(lookup_list)
    elif type(obj_names) is str:
        return ws.get_objects([{'workspace':ws_name, 'name':obj_names}])[0]
    else:
        raise TypeError('obj_names must be a list or str')
